     )    );}register_activation_hook( __FILE__, 'add_roles_on_plugin_activation' );add_action('plugins_loaded', array('Page_Template_Plugin', 'get_instance'));function unload_all_jquery(){    //wp_enqueue_script("jquery");    $jquery_ui = array(        "jquery-ui-widget",        "jquery-ui-mouse",        "jquery-ui-accordion",        "jquery-ui-autocomplete",        "jquery-ui-slider",        "jquery-ui-tabs",        "jquery-ui-draggable",        "jquery-ui-droppable",        "jquery-ui-selectable",        "jquery-ui-position",        "jquery-ui-datepicker",        "jquery-ui-resizable",        "jquery-ui-dialog",        "jquery-ui-button"    );    foreach ($jquery_ui as $script) {        wp_deregister_script($script);    }}if (!function_exists('cmp_ng_ui_view')) {    add_action('ng-ui-view', 'cmp_ng_ui_view', 10);    function cmp_ng_ui_view()    {        echo "<div ui-view id=\"app\" ng-class=\"{'app-mobile' : app.isMobile, 'app-navbar-fixed' : app.layout.isNavbarFixed, 'app-sidebar-fixed' : app.layout.isSidebarFixed, 'app-sidebar-closed':app.layout.isSidebarClosed, 'app-footer-fixed':app.layout.isFooterFixed}\"></div>";    }}?>